const series = msg.payload['results'][0].series[0];
const columns = series.columns; // ["time", "hash"]
const values = series.values;   // [["...", 1], ["...", 1]]

// Encontrar din√°micamente en qu√© posici√≥n est√° la columna 'hash'
const hashIndex = columns.indexOf('hash');
if (hashIndex === -1) {
    node.error("La columna 'hash' no se encontr√≥ en los resultados de InfluxDB.");
    return null; // Detiene el flujo si no existe la columna
}

// Usar un Set para extraer los hashes y eliminar duplicados autom√°ticamente
const topicSet = new Set();
for (const row of values) {
    // Extrae el hash usando el √≠ndice que encontramos
    const hash = row[hashIndex];
    if (hash !== null && hash !== undefined) {
        topicSet.add(`barrio/estaciones/${hash}`);
    }
}

// Si no se encontraron t√≥picos, detener el flujo
if (topicSet.size === 0) {
    node.warn("No se encontraron hashes v√°lidos para crear t√≥picos.");
    return null;
}

// Construir el payload final con los t√≥picos √∫nicos
msg.topic = Array.from(topicSet) // Convierte el Set a un Array
msg.action = "subscribe"

return msg;
